    /*
     * Editing a usermaster
     */
    function edit($userid)
    {   
        // check if the usermaster exists before trying to edit it
        $data['usermaster'] = $this->Usermaster_model->get_usermaster($userid);
        
        if(isset($data['usermaster']['userid']))
        {
            if(isset($_POST) && count($_POST) > 0)     
