					'useremail' => $this->input->post('useremail'),
					'usernumber' => $this->input->post('usernumber'),
					'userrole' => $this->input->post('userrole'),
					'school_id' => $this->input->post('school_id'),
					'location' => $this->input->post('location'),
					'team' => $this->input->post('team'),
					'phoneno' => $this->input->post('phoneno'),
					'name' => $this->input->post('name'),
                );

                $this->Usermaster_model->update_usermaster($userid,$params);            
                redirect('usermaster/index');
            }
            else
            {	
				$data['all_usermaster'] = $this->Usermaster_model->get_all_usermaster();
                $data['_view'] = 'usermaster/edit';
