    } 

    /*
     * Deleting usermaster
     */
    function remove($userid)
    {
        $usermaster = $this->Usermaster_model->get_usermaster($userid);

        // check if the usermaster exists before trying to delete it
        if(isset($usermaster['userid']))
        {
            $this->Usermaster_model->delete_usermaster($userid);
            redirect('usermaster/index');
        }
        else
            show_error('The usermaster you are trying to delete does not exist.');
    }
