	public function login() {
		
        $this->form_validation->set_rules('username', 'Username', 'required');
        $this->form_validation->set_rules('password', 'Password', 'required');
 
        if ($this->form_validation->run() == FALSE) {
            $this->load->view('layouts/login');
        } else {
 
            $username = $this->input->post('username');
            $password = $this->input->post('password'); 
			 
			$que=$this->db->query("select * from usermaster where username='".$username."' and userpwd='".$password."'");
			$row = $que->num_rows();
			
			if(!$row){
             
                $this->session->set_flashdata('login_error', "Please check your username or password and try again.", 300);
                redirect(uri_string());
            } 
			$user = $this->db->get_where('usermaster',['username' => $username])->row();
            /*if(!password_verify($password,$user->userpwd)) {
                $this->session->set_flashdata('login_error', 'Please check your username or password and try again.', 300);
                redirect(uri_string());
            }*/
 
             $data = array(
                    'user_name' => $user->name,
                    'email' => $user->useremail,
					'role'     => $user->userrole,
					'logged_in' => TRUE
                    ); 
			 
            $this->session->set_userdata($data);
 
            redirect('/'); // redirect to home
            echo 'Login success!'; exit; 
        }        
    }
 
    public function logout(){
